import React, { useState, useEffect } from 'react';
import CandlestickChart from '../components/CandlestickChart';
import '../styles/Markets.css';

const Markets = () => {
  const [stocks, setStocks] = useState([]);

  useEffect(() => {
    // Function to generate realistic candlestick data
    const generateCandlestickData = (basePrice, volatility, days) => {
      const data = [];
      let currentPrice = basePrice;
      const today = new Date();
      
      for (let i = days - 1; i >= 0; i--) {
        const date = new Date(today);
        date.setDate(date.getDate() - i);
        
        // Generate random price movement with more realistic patterns
        const open = currentPrice;
        const change = (Math.random() - 0.5) * volatility;
        const close = open * (1 + change);
        const high = Math.max(open, close) * (1 + Math.random() * 0.005);
        const low = Math.min(open, close) * (1 - Math.random() * 0.005);
        
        data.push({
          x: date.getTime(),
          y: [open, high, low, close].map(price => Math.round(price * 100) / 100)
        });
        
        currentPrice = close;
      }
      
      return data;
    };

    // Define stocks with their base prices and volatility
    const stockData = [
      { symbol: 'AAPL', name: 'Apple Inc.', basePrice: 170, volatility: 0.02 },
      { symbol: 'MSFT', name: 'Microsoft', basePrice: 420, volatility: 0.015 },
      { symbol: 'GOOGL', name: 'Alphabet', basePrice: 150, volatility: 0.018 },
      { symbol: 'AMZN', name: 'Amazon', basePrice: 180, volatility: 0.025 },
      { symbol: 'META', name: 'Meta', basePrice: 500, volatility: 0.022 },
      { symbol: 'TSLA', name: 'Tesla', basePrice: 170, volatility: 0.03 }
    ];

    // Generate data for each stock
    const stocksWithData = stockData.map(stock => ({
      ...stock,
      data: generateCandlestickData(stock.basePrice, stock.volatility, 30),
      currentPrice: 0,
      change: 0,
      changePercent: 0
    }));

    // Calculate current price and changes
    stocksWithData.forEach(stock => {
      const lastDay = stock.data[stock.data.length - 1];
      const prevDay = stock.data[stock.data.length - 2];
      stock.currentPrice = lastDay.y[3];
      stock.change = lastDay.y[3] - prevDay.y[3];
      stock.changePercent = (stock.change / prevDay.y[3]) * 100;
    });

    setStocks(stocksWithData);
  }, []);

  return (
    <div className="markets-page">
      <div className="container">
        <h1>Stock Market Overview</h1>
        <div className="stock-grid">
          {stocks.map((stock, index) => (
            <div key={index} className="stock-card">
              <div className="stock-symbol">{stock.symbol}</div>
              <div className="stock-name">{stock.name}</div>
              <div className="stock-price">${stock.currentPrice.toFixed(2)}</div>
              <div className={`stock-change ${stock.change >= 0 ? 'positive' : 'negative'}`}>
                {stock.change >= 0 ? '▲' : '▼'} ${Math.abs(stock.change).toFixed(2)} ({stock.changePercent.toFixed(2)}%)
              </div>
              <div className="chart-container">
                <CandlestickChart 
                  data={stock.data} 
                  title={stock.symbol}
                  height={200}
                  width={280}
                  margin={{ top: 10, right: 10, bottom: 20, left: 40 }}
                />
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Markets;
