import React from 'react';
import {
  ComposedChart,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer
} from 'recharts';

const CandlestickChart = ({ data, title, height, width, margin }) => {
  // Transform data for recharts
  const chartData = data.map(item => ({
    date: new Date(item.x).toLocaleDateString(),
    open: item.y[0],
    high: item.y[1],
    low: item.y[2],
    close: item.y[3],
    // Calculate if the candle is bullish (close > open) or bearish (close < open)
    isBullish: item.y[3] > item.y[0]
  }));

  // Custom candlestick component
  const CustomCandlestick = (props) => {
    const { x, y, width, height, open, close, high, low, isBullish } = props;
    const candleWidth = width * 0.8;
    const candleX = x + (width - candleWidth) / 2;
    const candleHeight = Math.abs(close - open);
    const candleY = Math.min(open, close);
    const wickHeight = high - low;
    const wickY = low;

    return (
      <g>
        {/* Wick */}
        <line
          x1={x + width / 2}
          y1={wickY}
          x2={x + width / 2}
          y2={wickY + wickHeight}
          stroke={isBullish ? '#00f962' : '#ff5a55'}
          strokeWidth={1}
        />
        {/* Candle body */}
        <rect
          x={candleX}
          y={candleY}
          width={candleWidth}
          height={candleHeight}
          fill={isBullish ? '#00f962' : '#ff5a55'}
        />
      </g>
    );
  };

  return (
    <div className="chart-container">
      <h3>{title}</h3>
      <ResponsiveContainer width={width} height={height}>
        <ComposedChart
          data={chartData}
          margin={margin}
        >
          <CartesianGrid strokeDasharray="3 3" stroke="#2a2a2a" />
          <XAxis 
            dataKey="date" 
            stroke="#e0e0e0"
            tick={{ fill: '#e0e0e0' }}
          />
          <YAxis 
            stroke="#e0e0e0"
            tick={{ fill: '#e0e0e0' }}
            domain={['auto', 'auto']}
          />
          <Tooltip
            contentStyle={{
              backgroundColor: 'rgba(30, 30, 30, 0.9)',
              border: '1px solid rgba(255, 255, 255, 0.1)',
              borderRadius: '8px',
              color: '#e0e0e0'
            }}
            formatter={(value, name) => {
              if (name === 'Open' || name === 'High' || name === 'Low' || name === 'Close') {
                return [`$${value.toFixed(2)}`, name];
              }
              return [value, name];
            }}
          />
          <Legend />
          {chartData.map((entry, index) => (
            <CustomCandlestick
              key={index}
              x={index * (width / chartData.length)}
              y={0}
              width={width / chartData.length}
              height={height}
              open={entry.open}
              close={entry.close}
              high={entry.high}
              low={entry.low}
              isBullish={entry.isBullish}
            />
          ))}
        </ComposedChart>
      </ResponsiveContainer>
    </div>
  );
};

export default CandlestickChart;
