import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import { HomeRounded, ShowChartRounded, ArticleRounded, LoginRounded } from '@mui/icons-material';
import '../styles/components/Navbar.scss';

function Navbar() {
  const location = useLocation();

  const isActive = (path) => {
    return location.pathname === path ? 'active' : '';
  };

  return (
    <nav className="navbar">
      <div className="navbar-container">
        <div className="navbar-brand">
          <Link to="/">
            <span className="logo1">Stock</span>
            <span className="logo2">Overflow</span>
          </Link>
        </div>
        <div className="navbar-links">
          <Link to="/" className={isActive('/')}>
            <HomeRounded sx={{ mr: 0.5 }} />
            Home
          </Link>
          <Link to="/markets" className={isActive('/markets')}>
            <ShowChartRounded sx={{ mr: 0.5 }} />
            Markets
          </Link>
          <Link to="/news" className={isActive('/news')}>
            <ArticleRounded sx={{ mr: 0.5 }} />
            News
          </Link>
          <Link to="/login" className="login-btn">
            <LoginRounded sx={{ mr: 0.5 }} />
            Login
          </Link>
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
