import React from 'react';
import {
  ComposedChart,
  Line,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ReferenceLine,
} from 'recharts';

const CandlestickChart = ({ data, width = 800, height = 400 }) => {
  return (
    <ComposedChart width={width} height={height} data={data}>
      <CartesianGrid strokeDasharray="3 3" />
      <XAxis dataKey="date" />
      <YAxis domain={['auto', 'auto']} />
      <Tooltip
        content={({ active, payload }) => {
          if (active && payload && payload.length) {
            const data = payload[0].payload;
            return (
              <div className="custom-tooltip">
                <p className="date">{data.date}</p>
                <p className="open">Open: ${data.open.toFixed(2)}</p>
                <p className="close">Close: ${data.close.toFixed(2)}</p>
                <p className="high">High: ${data.high.toFixed(2)}</p>
                <p className="low">Low: ${data.low.toFixed(2)}</p>
                <p className="volume">Volume: {data.volume.toLocaleString()}</p>
              </div>
            );
          }
          return null;
        }}
      />
      <Legend />
      {/* Candlestick body */}
      <Bar
        dataKey="candlestick"
        fill="transparent"
        stroke="none"
        data={data.map(d => ({
          ...d,
          candlestick: [d.open, d.close],
          fill: d.open > d.close ? '#ff5a55' : '#00f962'
        }))}
      >
        {data.map((entry, index) => (
          <rect
            key={`candlestick-${index}`}
            x={index * (width / data.length) + width / (2 * data.length) - 5}
            y={Math.min(entry.open, entry.close)}
            width={10}
            height={Math.abs(entry.open - entry.close)}
            fill={entry.open > entry.close ? '#ff5a55' : '#00f962'}
          />
        ))}
      </Bar>
      {/* High-Low lines */}
      {data.map((entry, index) => (
        <Line
          key={`hl-${index}`}
          type="monotone"
          data={[
            { x: index, y: entry.low },
            { x: index, y: entry.high }
          ]}
          stroke={entry.open > entry.close ? '#ff5a55' : '#00f962'}
          dot={false}
        />
      ))}
    </ComposedChart>
  );
};

export default CandlestickChart;
