import React from 'react';
import { ResponsiveContainer, XAxis, YAxis, CartesianGrid, Tooltip, ComposedChart, Line } from 'recharts';

const CandlestickChart = ({ data, title, height, width, margin }) => {
  const CustomTooltip = ({ active, payload, label }) => {
    if (active && payload && payload.length) {
      const date = new Date(label).toLocaleDateString();
      const [open, high, low, close] = payload[0].payload.y;
      
      return (
        <div className="custom-tooltip" style={{
          backgroundColor: '#1e1e1e',
          border: '1px solid #333',
          padding: '10px',
          borderRadius: '4px',
          color: '#e0e0e0'
        }}>
          <p style={{ margin: '0 0 5px 0', color: '#bb86fc' }}>{date}</p>
          <p style={{ margin: '0 0 5px 0' }}>Open: ${open.toFixed(2)}</p>
          <p style={{ margin: '0 0 5px 0' }}>High: ${high.toFixed(2)}</p>
          <p style={{ margin: '0 0 5px 0' }}>Low: ${low.toFixed(2)}</p>
          <p style={{ margin: '0' }}>Close: ${close.toFixed(2)}</p>
        </div>
      );
    }
    return null;
  };

  // Transform data for recharts
  const chartData = data.map(item => ({
    date: item.x,
    open: item.y[0],
    high: item.y[1],
    low: item.y[2],
    close: item.y[3]
  }));

  return (
    <div style={{ width: '100%', height: height }}>
      <ResponsiveContainer>
        <ComposedChart
          data={chartData}
          margin={margin}
        >
          <CartesianGrid strokeDasharray="3 3" stroke="#333" />
          <XAxis
            dataKey="date"
            tickFormatter={(time) => new Date(time).toLocaleDateString()}
            stroke="#e0e0e0"
          />
          <YAxis
            domain={['dataMin', 'dataMax']}
            stroke="#e0e0e0"
          />
          <Tooltip content={<CustomTooltip />} />
          <Line
            type="monotone"
            dataKey="close"
            stroke="#ff5a55"
            dot={false}
            strokeWidth={2}
          />
          <Line
            type="monotone"
            dataKey="high"
            stroke="#00f962"
            dot={false}
            strokeWidth={1}
          />
          <Line
            type="monotone"
            dataKey="low"
            stroke="#00f962"
            dot={false}
            strokeWidth={1}
          />
        </ComposedChart>
      </ResponsiveContainer>
    </div>
  );
};

export default CandlestickChart;
