import { createTheme } from '@mui/material/styles';

const theme = createTheme({
  palette: {
    mode: 'dark',
    primary: {
      main: '#38efe6',
      light: '#5ff4ed',
      dark: '#2bb8b1',
    },
    secondary: {
      main: '#2a2a2a',
    },
    background: {
      default: '#121212',
      paper: '#1e1e1e',
    },
    text: {
      primary: '#ffffff',
      secondary: 'rgba(255, 255, 255, 0.7)',
    },
    success: {
      main: '#4caf50',
    },
    error: {
      main: '#f44336',
    },
    warning: {
      main: '#ff9800',
    },
  },
  typography: {
    fontFamily: "'Inter', -apple-system, BlinkMacSystemFont, sans-serif",
    h1: {
      fontFamily: "'Mohave', sans-serif",
      fontWeight: 700,
    },
    h2: {
      fontFamily: "'Mohave', sans-serif",
      fontWeight: 700,
    },
    h3: {
      fontFamily: "'Mohave', sans-serif",
      fontWeight: 700,
    },
    h4: {
      fontFamily: "'Mohave', sans-serif",
      fontWeight: 700,
    },
    h5: {
      fontFamily: "'Mohave', sans-serif",
      fontWeight: 700,
    },
    h6: {
      fontFamily: "'Mohave', sans-serif",
      fontWeight: 700,
    },
  },
  shape: {
    borderRadius: 8,
  },
  components: {
    MuiButton: {
      styleOverrides: {
        root: {
          textTransform: 'none',
          fontWeight: 500,
        },
      },
    },
    MuiCard: {
      styleOverrides: {
        root: {
          backgroundColor: '#2a2a2a',
          backgroundImage: 'none',
        },
      },
    },
  },
});

export default theme;
